mapper.select:

	SELECT 
    A.* ,
    (SELECT COUNT(*)FROM BOARD_LIKE WHERE 1=1 AND LIKE_SEQ_NO = #{seqno}) LIKE_CNT2
    FROM BOARD A
	WHERE 1=1
	AND SEQ_NO = #{seqno}
	AND BOARD_TYPE = #{boardType}
	AND BOARD_SUB_TYPE = #{boardSubType}


  SELECT * FROM BOARD WHERE seq_no = (
		 SELECT MAX(SEQ_NO)
		 FROM BOARD
		 WHERE SEQ_NO < #{seqno}
		 AND BOARD_TYPE = #{boardType}
		 AND BOARD_SUB_TYPE = #{boardSubType}
	)


  SELECT * FROM BOARD WHERE seq_no = (
		 SELECT MIN(SEQ_NO)
		 FROM BOARD
		 WHERE 1=1
		 AND SEQ_NO > #{seqno}
		 AND BOARD_TYPE = #{boardType}
		 AND BOARD_SUB_TYPE = #{boardSubType}
	)

mapper.update:
UPDATE BOARD
SET LIKE_CNT = LIKE_CNT + 1
WHERE seq_no = #{seqno}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}

mapper.select:
SELECT 
COUNT(*) LIKE_CNT
FROM BOARD_LIKE 
WHERE 1=1
AND LIKE_SEQ_NO = #{seqno}
AND BOARD_TYPE = #{boardType}

SELECT COUNT(*) LIKE_CNT
FROM BOARD_LIKE
WHERE 1=1
AND LIKE_SEQ_NO = #{seqno}
AND M_ID = #{m_id}
AND BOARD_TYPE = #{boardType}

mapper.update:

UPDATE BOARD
SET VIEW_CNT = VIEW_CNT + 1
WHERE 1=1
AND seq_no = #{seqno}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}

mapper.delete:
DELETE FROM BOARD
WHERE 1=1
AND SEQ_NO = #{seqno}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}

mapper.update:

UPDATE BOARD SET 
    TITLE = #{title}
,   CONTENTS = #{contents}
,   MOD_ID = #{modid}
,   MOD_DTM = (SELECT TO_CHAR(SYSDATE, 'YYYY/MM/DD HH24:MI:SS') FROM DUAL)
WHERE 1=1
AND seq_no = #{seqno}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}

mapper.select:
SELECT
    B.*
	FROM BOARD B
	WHERE 1=1
    AND BOARD_TYPE = #{boardType}
    AND PRT_SEQNO = #{prt_seqno}
	AND BOARD_SUB_TYPE = #{boardSubType}
	ORDER BY GRP_SEQNO DESC, regnt_dtm asc

mapper.update:
UPDATE BOARD
SET CONTENTS = #{contents}
WHERE seq_no = #{seqno}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}

mapper.delete:
DELETE FROM BOARD
WHERE 1=1
AND SEQ_NO = #{seqno}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}
