mapper.update:

UPDATE PDS_BOARD SET
VIEWS = (SELECT MAX(NVL(VIEWS,0)) + 1 FROM PDS_BOARD WHERE SEQ = #{seq} ) 
WHERE SEQ = #{seq}
AND	BOARD_TYPE = #{boardType}
AND	DEL_YN = 'N'

mapper.select:

  SELECT * FROM BOARD WHERE seq_no = (
		 SELECT MAX(SEQ_NO)
		 FROM BOARD
		 WHERE SEQ_NO < #{seqno}
		AND BOARD_TYPE = '30'
	)


  SELECT * FROM BOARD WHERE seq_no = (
		 SELECT MIN(SEQ_NO)
		 FROM BOARD
		 WHERE SEQ_NO > #{seqno}
		AND BOARD_TYPE = '30'
	)

mapper.update:
UPDATE PDS_BOARD SET
TITLE = #{sTitle}, CONTENTS = #{sContent}, NOTE = #{sNote}, MOD_ID = #{modId}, MOD_DATE = SYSDATE, ORIGIN_FILE_NAME1= #{originFileName1}, TARGET_FILE_NAME1= #{targetFileName1}, FILE_SIZE1= #{fileSize1}, FILE_EXT1= #{fileExt1}, ORIGIN_FILE_NAME2= #{originFileName2}, TARGET_FILE_NAME2= #{targetFileName2}, FILE_SIZE2= #{fileSize2}, FILE_EXT2= #{fileExt2}, ORIGIN_FILE_NAME3= #{originFileName3}, TARGET_FILE_NAME3= #{targetFileName3}, FILE_SIZE3= #{fileSize3}, FILE_EXT3= #{fileExt3}, ORIGIN_FILE_NAME4= #{originFileName4}, TARGET_FILE_NAME4= #{targetFileName4}, FILE_SIZE4= #{fileSize4}, FILE_EXT4= #{fileExt4}, FILE_PATH= #{file_path}
WHERE SEQ = #{seq} AND BOARD_TYPE = #{boardType}

mapper.delete:
DELETE FROM PDS_BOARD 
WHERE SEQ = #{seq}
AND BOARD_TYPE = #{boardType}

mapper.select:
SELECT * FROM PDS_BOARD 
WHERE SEQ = #{seq}
AND BOARD_TYPE = #{boardType}

mapper.update:
UPDATE BOARD
SET LIKE_CNT = LIKE_CNT + 1
WHERE seq_no = #{seqno}

mapper.select:
SELECT COUNT(*) LIKE_CNT
FROM BOARD_LIKE
WHERE 1=1
AND LIKE_SEQ_NO = #{seqno}
AND M_ID = #{m_id}

mapper.update:

UPDATE BOARD
SET VIEW_CNT = VIEW_CNT + 1
WHERE seq_no = #{seqno}


UPDATE BOARD SET 
    TITLE = #{title}
,   CONTENTS = #{contents}
,   MOD_ID = #{modid}
,   MOD_DTM = (SELECT TO_CHAR(SYSDATE, 'YYYY/MM/DD HH24:MI:SS') FROM DUAL)
WHERE seq_no = #{seqno}
